Scripts.OnLoad:
getglobal(self:GetName().."Text"):SetText("|cff88ff88DHUD 1|r (Hitpoints Left - Mana Right)");
if DHUD:GetConfig("layouttyp") == "DHUD_Standard_Layout" then
    DHUD_RADIO_layout1:SetChecked(1);
else
    DHUD_RADIO_layout1:SetChecked(nil);
end

getglobal(self:GetName().."Text"):SetText("|cff88ff88DHUD 2|r (Player Left - Target Right)");
if DHUD:GetConfig("layouttyp") == "DHUD_PlayerLeft_Layout" then
    DHUD_RADIO_layout2:SetChecked(1);
else
    DHUD_RADIO_layout2:SetChecked(nil);
end

